(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[32778],{32778:e=>{e.exports="import {Component, inject, signal} from '@angular/core';\nimport {FormControl, ReactiveFormsModule} from '@angular/forms';\nimport {changeDetection} from '@demo/emulate/change-detection';\nimport {encapsulation} from '@demo/emulate/encapsulation';\nimport {TuiRepeatTimes} from '@taiga-ui/cdk';\nimport {\n    TuiButton,\n    TuiDialogService,\n    TuiHeader,\n    TuiPopup,\n    TuiTextfield,\n    TuiTitle,\n} from '@taiga-ui/core';\nimport {TUI_CONFIRM, TuiDrawer} from '@taiga-ui/kit';\nimport {filter} from 'rxjs';\n\n@Component({\n    imports: [\n        ReactiveFormsModule,\n        TuiButton,\n        TuiDrawer,\n        TuiHeader,\n        TuiPopup,\n        TuiRepeatTimes,\n        TuiTextfield,\n        TuiTitle,\n    ],\n    templateUrl: './index.html',\n    styleUrl: './index.less',\n    encapsulation,\n    changeDetection,\n})\nexport default class Example {\n    protected readonly dialogs = inject(TuiDialogService);\n    protected readonly control = new FormControl('Some value');\n    protected readonly open = signal(false);\n\n    public onClose(): void {\n        if (this.control.pristine) {\n            this.open.set(false);\n\n            return;\n        }\n\n        this.dialogs\n            .open(TUI_CONFIRM, {\n                label: 'Cancel editing form?',\n                size: 's',\n                data: {\n                    content: 'You have unsaved changes that will be lost',\n                },\n            })\n            .pipe(filter(Boolean))\n            .subscribe(() => {\n                this.open.set(false);\n                this.control.reset('Some value');\n            });\n    }\n}\n"}}]);