(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[36037],{36037:n=>{n.exports='<button\n    size="s"\n    tuiButton\n    (click)="add()"\n>\n    Add item\n</button>\n<tui-elastic-container class="tui-space_top-4">\n    <div\n        *ngFor="let item of items; let index = index"\n        class="wrapper"\n    >\n        <h3 class="title">\n            <button\n                appearance="secondary"\n                size="s"\n                tuiIconButton\n                class="tui-space_right-2"\n                [style.border-radius.%]="100"\n                [tuiChevron]="item.expanded"\n                (click)="item.expanded = !item.expanded"\n            >\n                Expand\n            </button>\n            Nested form\n            <button\n                appearance="flat"\n                iconStart="@tui.trash"\n                size="s"\n                tuiIconButton\n                class="remove"\n                [style.border-radius.%]="100"\n                (click)="remove(index)"\n            >\n                Remove\n            </button>\n        </h3>\n        <tui-expand [expanded]="item.expanded">\n            <tui-input\n                class="tui-space_top-4"\n                [(ngModel)]="item.value"\n            >\n                Some input\n            </tui-input>\n        </tui-expand>\n    </div>\n</tui-elastic-container>\n'}}]);