(self.webpackChunk_taiga_ui_components=self.webpackChunk_taiga_ui_components||[]).push([[36037],{36037:n=>{n.exports='<button\n    size="s"\n    tuiButton\n    (click)="add()"\n>\n    Add item\n</button>\n<tui-elastic-container class="b-form tui-space_top-4">\n    <div\n        *ngFor="let item of items; let index = index"\n        class="wrapper"\n    >\n        <h3 class="title">\n            <button\n                appearance="secondary"\n                icon="tuiIconChevronDown"\n                shape="rounded"\n                size="s"\n                title="Expand"\n                tuiIconButton\n                class="expand"\n                [class.expand_open]="item.expanded"\n                (click)="item.expanded = !item.expanded"\n            ></button>\n            Nested form\n            <button\n                appearance="flat"\n                icon="tuiIconTrash"\n                shape="rounded"\n                size="s"\n                title="Remove"\n                tuiIconButton\n                class="remove"\n                (click)="remove(index)"\n            ></button>\n        </h3>\n        <tui-expand [expanded]="item.expanded">\n            <tui-input\n                class="tui-space_top-4"\n                [(ngModel)]="item.value"\n            >\n                Some input\n            </tui-input>\n        </tui-expand>\n    </div>\n</tui-elastic-container>\n'}}]);