(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[38695],{38695:n=>{n.exports='<section>\n    @for (size of sizes; track size) {\n        <tui-chip\n            [size]="size"\n            [style.border-radius.rem]="5"\n        >\n            Text\n        </tui-chip>\n    }\n</section>\n\n<section>\n    @for (size of sizes; track size) {\n        <tui-chip\n            iconStart="@tui.bell"\n            [size]="size"\n        >\n            Single icon\n        </tui-chip>\n    }\n</section>\n\n<section>\n    @for (size of sizes; track size) {\n        <tui-chip\n            iconEnd="@tui.circle-help"\n            iconStart="@tui.bell"\n            [size]="size"\n            [style.border-radius.rem]="5"\n        >\n            Duo icon\n        </tui-chip>\n    }\n</section>\n\n<section>\n    @for (size of sizes; track size) {\n        <tui-chip [size]="size">\n            @if (size === \'m\' || size === \'s\') {\n                <img\n                    alt="Avatar"\n                    src="/assets/images/avatar.jpg"\n                    [style.border-radius.rem]="size === \'m\' ? 0.25 : 0.5"\n                />\n            }\n            Image\n        </tui-chip>\n    }\n</section>\n\n<section>\n    @for (size of sizes; track size) {\n        <tui-chip\n            [size]="size"\n            [style.border-radius.rem]="5"\n        >\n            @if (size === \'m\' || size === \'s\') {\n                <div\n                    tuiAvatar="@tui.user"\n                    [round]="true"\n                >\n                    <img\n                        alt=""\n                        src="/assets/images/avatar.jpg"\n                    />\n                </div>\n            }\n            Avatar\n        </tui-chip>\n    }\n</section>\n\n<section>\n    <tui-chip size="m">\n        Button\n        <button\n            iconStart="@tui.x"\n            size="s"\n            tuiIconButton\n            type="button"\n        >\n            Remove\n        </button>\n    </tui-chip>\n    @for (size of sizes | slice: 1; track size) {\n        <tui-chip [size]="size">\n            Button\n            <button\n                iconStart="@tui.x"\n                tuiIconButton\n                type="button"\n            >\n                Remove\n            </button>\n        </tui-chip>\n    }\n</section>\n\n<section>\n    @for (size of sizes; track size) {\n        <tui-chip [size]="size">\n            Badge\n\n            @if (size !== \'xxs\') {\n                <tui-badge\n                    appearance="primary"\n                    [size]="size === \'m\' ? \'m\' : \'s\'"\n                >\n                    1\n                </tui-badge>\n            }\n\n            <button\n                iconStart="@tui.x"\n                tuiIconButton\n                type="button"\n            >\n                Remove\n            </button>\n        </tui-chip>\n    }\n</section>\n'}}]);