(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[41007],{41007:n=>{n.exports='<tui-stepper\n    [activeItemIndex]="index"\n    (activeItemIndexChange)="onStep($event)"\n>\n    <button tuiStep>Personal details</button>\n    <button tuiStep>Shipping address</button>\n    <button tuiStep>Payment info</button>\n</tui-stepper>\n<section [tuiSlides]="direction">\n    <ng-container *ngFor="let form of forms; let i = index">\n        <form\n            *ngIf="i === index"\n            tuiCardLarge\n            tuiForm\n            tuiSurface="floating"\n            [formGroup]="form"\n            [id]="\'form_\' + i"\n            (ngSubmit)="onSubmit()"\n        >\n            <header tuiHeader>\n                <h2 tuiTitle>\n                    Registration form\n                    <span tuiSubtitle>Tell us about yourself</span>\n                </h2>\n            </header>\n            <tui-textfield *ngFor="let control of form.controls | keyvalue; let j = index">\n                <label tuiLabel>{{ control.key }}</label>\n                <input\n                    tuiTextfield\n                    [formControlName]="control.key"\n                    [tuiAutoFocus]="!!index && !j"\n                />\n            </tui-textfield>\n        </form>\n    </ng-container>\n</section>\n<footer>\n    <button\n        appearance="secondary"\n        tuiButton\n        type="button"\n        [disabled]="!index"\n        (click)="index = index - 1"\n    >\n        Back\n    </button>\n\n    <button\n        tuiButton\n        type="submit"\n        [attr.form]="\'form_\' + index"\n    >\n        Next\n    </button>\n</footer>\n'}}]);