(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[48146],{48146:n=>{n.exports='<p>\n    Your balance:\n    <span>{{ money | tuiAmount: \'RUB\' | async }}</span>\n</p>\n<button\n    size="m"\n    tuiButton\n    type="button"\n    (click)="showDialog(template)"\n>\n    Show\n</button>\n\n<ng-template\n    #template\n    let-observer\n>\n    <p>You can show a dialog with template</p>\n    <p>\n        Your balance:\n        <span>{{ money | tuiAmount: \'RUB\' | async }}</span>\n    </p>\n    <button\n        size="m"\n        tuiButton\n        tuiChevron\n        tuiDropdownOpen\n        type="button"\n        class="tui-space_right-3"\n        [tuiDropdown]="dropdown"\n    >\n        Withdraw\n        <ng-template\n            #dropdown\n            let-close\n        >\n            <tui-data-list>\n                <button\n                    *ngFor="let sum of [10, 50, 100]"\n                    tuiOption\n                    (click)="withdraw(sum); close()"\n                >\n                    {{ sum | tuiAmount: \'RUB\' | async }}\n                </button>\n            </tui-data-list>\n        </ng-template>\n    </button>\n    <button\n        size="m"\n        tuiButton\n        type="button"\n        (click)="observer.complete()"\n    >\n        Cancel\n    </button>\n</ng-template>\n'}}]);