(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[57933],{57933:n=>{n.exports='<form\n    class="container"\n    [formGroup]="testForm"\n>\n    <tui-input\n        *tuiLet="users$ | async as users"\n        formControlName="user"\n        [tuiTextfieldCustomContent]="content"\n    >\n        User\n        <input\n            placeholder="Type your name or surname"\n            tuiTextfieldLegacy\n        />\n        <ng-container *ngIf="users?.length">\n            <tui-data-list *tuiDataList>\n                <button\n                    *ngFor="let user of users"\n                    tuiOption\n                    [value]="user.toString()"\n                    (click)="onSelected(user)"\n                >\n                    {{ user }}\n                    <tui-avatar\n                        size="xs"\n                        [src]="user.avatarUrl"\n                    >\n                        {{ user.toString() | tuiInitials }}\n                    </tui-avatar>\n                </button>\n            </tui-data-list>\n        </ng-container>\n    </tui-input>\n    <ng-template #avatar>\n        <tui-avatar\n            *ngIf="lastUser"\n            size="s"\n            [src]="lastUser.avatarUrl"\n        >\n            {{ lastUser.toString() | tuiInitials }}\n        </tui-avatar>\n    </ng-template>\n    <tui-input\n        formControlName="account"\n        class="tui-space_vertical-5"\n    >\n        Account\n        <tui-data-list-wrapper\n            *tuiDataList\n            [itemContent]="accountContent"\n            [items]="accounts"\n        />\n        <ng-template\n            #accountContent\n            let-account\n        >\n            <div>\n                {{ account.name }}\n                <span>{{ account.amount | tuiAmount: \'RUB\' | async }}</span>\n            </div>\n        </ng-template>\n    </tui-input>\n    <tui-textfield>\n        <label tuiLabel>Card number</label>\n        <input\n            formControlName="card"\n            tuiInputCard\n            tuiTextfield\n            [icon]="card"\n        />\n    </tui-textfield>\n</form>\n'}}]);