(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[71805],{71805:n=>{n.exports='<button\n    tuiButton\n    type="button"\n    (click)="open = true"\n>\n    Show\n</button>\n\n<ng-template #template>\n    <div tuiHeader>\n        <div tuiTitle>\n            Title\n            <div tuiSubtitle>Subtitle</div>\n        </div>\n    </div>\n    <label\n        tuiLabel\n        [style.margin-top.rem]="1"\n    >\n        <input\n            tuiSwitch\n            type="checkbox"\n            [(ngModel)]="floating"\n        />\n        Floating visibility\n    </label>\n    <label\n        tuiLabel\n        [style.margin-top.rem]="0.5"\n    >\n        <input\n            tuiSwitch\n            type="checkbox"\n            [(ngModel)]="secondAction"\n        />\n        Second action visibility\n    </label>\n    <input\n        placeholder="Some search"\n        tuiSearch\n        type="search"\n        class="input"\n        [(ngModel)]="search"\n    />\n</ng-template>\n\n<ng-template\n    let-observer\n    [tuiSheetDialogOptions]="{label: template, fullscreen: true}"\n    [(tuiSheetDialog)]="open"\n>\n    <div class="content">\n        <section class="items">\n            <div\n                *ngFor="let item of items | tuiFilter: filter : search"\n                tuiCell\n            >\n                <tui-avatar\n                    appearance="primary"\n                    src="@tui.star"\n                />\n                <div tuiTitle>\n                    {{ item.title }}\n                    <div tuiSubtitle>{{ item.description }}</div>\n                </div>\n            </div>\n        </section>\n\n        <footer\n            *ngIf="floating"\n            tuiFloatingContainer\n            @tuiSlideInTop\n        >\n            <button\n                tuiButton\n                type="button"\n                (click)="observer.complete()"\n            >\n                Main action\n            </button>\n            <button\n                *ngIf="secondAction"\n                appearance="flat"\n                tuiButton\n                type="button"\n                @tuiHeightCollapse\n                (click)="observer.complete()"\n            >\n                Secondary action\n            </button>\n        </footer>\n    </div>\n</ng-template>\n'}}]);