(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[72581],{72581:n=>{n.exports="import type {OnInit} from '@angular/core';\nimport {ChangeDetectorRef, Component, DestroyRef, inject, NgZone} from '@angular/core';\nimport {takeUntilDestroyed} from '@angular/core/rxjs-interop';\nimport {FormsModule} from '@angular/forms';\nimport {changeDetection} from '@demo/emulate/change-detection';\nimport {encapsulation} from '@demo/emulate/encapsulation';\nimport {TUI_IS_E2E, tuiWatch, tuiZoneOptimized} from '@taiga-ui/cdk';\nimport {TuiButton} from '@taiga-ui/core';\nimport {TuiInputInline} from '@taiga-ui/kit';\nimport {timer} from 'rxjs';\n\n@Component({\n    standalone: true,\n    exportAs: \"Example3\",\n    imports: [TuiInputInline, TuiButton, FormsModule],\n    templateUrl: './index.html',\n    styleUrls: ['./index.less'],\n    encapsulation,\n    changeDetection,\n})\nexport default class Example implements OnInit {\n    private readonly cd = inject(ChangeDetectorRef);\n    private readonly destroyRef = inject(DestroyRef);\n    private readonly zone = inject(NgZone);\n    protected readonly isE2E = inject(TUI_IS_E2E);\n\n    protected count = '0';\n\n    public ngOnInit(): void {\n        if (this.isE2E) {\n            return;\n        }\n\n        timer(0, 3000)\n            .pipe(\n                tuiZoneOptimized(this.zone),\n                tuiWatch(this.cd),\n                takeUntilDestroyed(this.destroyRef),\n            )\n            .subscribe((value) => {\n                this.count = String(value);\n            });\n    }\n}\n"}}]);