(self.webpackChunk_taiga_ui_components=self.webpackChunk_taiga_ui_components||[]).push([[76162],{76162:n=>{n.exports='<button\n    tuiSurface\n    class="card"\n    [style.background-image]="\'url(/assets/images/tickets.svg)\'"\n    [style.color]="\'#d45d8c\'"\n>\n    <h3 class="heading">Tickets</h3>\n    <div class="content">Concerts, theater, sports and movies</div>\n    <tui-badge\n        appearance="primary"\n        size="s"\n        class="badge"\n    >\n        20% off\n    </tui-badge>\n</button>\n<button\n    tuiSurface\n    class="card"\n    [style.background-image]="\'url(/assets/images/gas.svg)\'"\n    [style.color]="\'#7caeff\'"\n>\n    <h3 class="heading">Gas</h3>\n    <tui-badge\n        appearance="primary"\n        size="s"\n        class="badge"\n    >\n        +2000%\n    </tui-badge>\n</button>\n\n<div\n    tuiSurface\n    class="restaurant"\n>\n    <header\n        tuiMode="onDark"\n        class="header"\n    >\n        <label tuiLabel="RESTAURANT">Eat all you can</label>\n        <button\n            appearance="icon"\n            iconLeft="tuiIconMoreHorizontalLarge"\n            size="xs"\n            tuiIconButton\n        >\n            More\n        </button>\n    </header>\n    <footer class="footer">\n        <button\n            appearance="glass"\n            size="m"\n            tuiButton\n            [style.border-radius.rem]="5"\n        >\n            Book a table\n        </button>\n        <button\n            appearance="glass"\n            iconLeft="tuiIconHeartLarge"\n            size="m"\n            tuiIconButton\n            [style.border-radius.rem]="5"\n        >\n            Favorite\n        </button>\n    </footer>\n</div>\n\n<div\n    tuiMode="onDark"\n    tuiSurface\n    class="blur"\n>\n    <tui-avatar src="/assets/images/avatar.jpg"></tui-avatar>\n    <label tuiLabel="Taiga UI">Alex Inkin</label>\n    <button\n        appearance="glass"\n        iconLeft="tuiIconMailLarge"\n        size="m"\n        tuiIconButton\n        class="button"\n    >\n        Message\n    </button>\n</div>\n\n<div\n    tuiCardLarge="compact"\n    tuiSurface="elevated"\n    class="reviews"\n>\n    <h3 class="heading">\n        Taiga UI reviews\n        <button\n            tuiLink\n            class="link"\n        >\n            Hide\n        </button>\n    </h3>\n    <tui-scrollbar\n        class="scrollbar"\n        [hidden]="true"\n    >\n        <div class="wrapper">\n            <div\n                *ngFor="let review of reviews"\n                tuiSurface="flat"\n                class="review"\n            >\n                {{ review.body }}\n                <footer class="footer">\n                    <tui-avatar\n                        size="s"\n                        src="/assets/images/avatar.jpg"\n                    ></tui-avatar>\n                    <label\n                        class="label"\n                        [tuiLabel]="review.time"\n                    >\n                        {{ review.name }}\n                    </label>\n                </footer>\n            </div>\n        </div>\n    </tui-scrollbar>\n    <button\n        appearance="secondary"\n        size="m"\n        tuiButton\n    >\n        See all\n    </button>\n</div>\n\n<div\n    tuiMode="onDark"\n    tuiSurface\n    class="mask"\n>\n    <h3 class="heading">\n        My bank account\n        <button\n            appearance="secondary"\n            iconLeft="tuiIconEdit2"\n            size="s"\n            tuiIconButton\n            class="button"\n        >\n            Edit name\n        </button>\n    </h3>\n    <tui-money\n        currency="$"\n        currencyAlign="left"\n        class="sum"\n        [value]="23742"\n    ></tui-money>\n    <footer class="footer">\n        <tui-thumbnail-card\n            cardNumber="1234"\n            paymentSystem="mastercard"\n            [style.background]="\'#337\'"\n        ></tui-thumbnail-card>\n        <tui-thumbnail-card\n            cardNumber="5678"\n            paymentSystem="visa"\n            [style.background]="\'#e33\'"\n        ></tui-thumbnail-card>\n        <button\n            appearance="secondary"\n            iconLeft="tuiIconPlus"\n            size="s"\n            tuiIconButton\n            class="add"\n        >\n            Add card\n        </button>\n    </footer>\n</div>\n'}}]);