(self.webpackChunk_taiga_ui_components=self.webpackChunk_taiga_ui_components||[]).push([[77340],{77340:n=>{n.exports='<tui-hosted-dropdown\n    tuiDropdownLimitWidth="fixed"\n    class="example"\n    [content]="content"\n    [(open)]="dropdownOpen"\n>\n    <button\n        appearance="outline"\n        icon="tuiIconMoreVertical"\n        size="m"\n        tuiButton\n        type="button"\n        class="example"\n    >\n        List of components\n    </button>\n</tui-hosted-dropdown>\n\n<ng-template\n    #content\n    let-activeZone\n>\n    <tui-data-list\n        tuiDataListDropdownManager\n        [tuiActiveZoneParent]="activeZone"\n    >\n        <button\n            tuiDropdownAlign="right"\n            tuiDropdownDirection="top"\n            tuiDropdownSided\n            tuiOption\n            [tuiDropdown]="money"\n            [tuiDropdownManual]="true"\n        >\n            ðŸ’° Money:\n        </button>\n        <button\n            automation-id="tui-data-list-calendar-option"\n            tuiDropdownAlign="right"\n            tuiDropdownSided\n            tuiOption\n            [tuiDropdown]="calendar"\n            [tuiDropdownManual]="false"\n        >\n            ðŸ“… Calendar: {{ dateValue }}\n        </button>\n        <button\n            automation-id="tui-data-list-email-option"\n            tuiDropdownAlign="right"\n            tuiDropdownSided\n            tuiOption\n            [tuiDropdown]="input"\n            [tuiDropdownManual]="false"\n        >\n            ðŸ“§ Email: {{ testValue }}\n        </button>\n        <button\n            automation-id="tui-data-list-range-option"\n            tuiDropdownAlign="right"\n            tuiDropdownDirection="top"\n            tuiDropdownSided\n            tuiOption\n            [tuiDropdown]="range"\n            [tuiDropdownManual]="false"\n        >\n            âŒ› Range: {{ rangeValue }}\n        </button>\n    </tui-data-list>\n\n    <ng-template #money>\n        <form\n            class="form"\n            [formGroup]="moneyForm"\n        >\n            <tui-input\n                automation-id="tui-data-money-input"\n                formControlName="moneyValue"\n            >\n                RUB\n            </tui-input>\n        </form>\n\n        <tui-data-list\n            tuiDataListDropdownManager\n            [tuiActiveZoneParent]="activeZone"\n        >\n            <button\n                tuiDropdownAlign="right"\n                tuiDropdownDirection="bottom"\n                tuiDropdownSided\n                tuiOption\n                [tuiDropdown]="currency"\n                [tuiDropdownManual]="true"\n            >\n                Exchange Rates:\n            </button>\n        </tui-data-list>\n    </ng-template>\n\n    <ng-template #calendar>\n        <tui-calendar\n            [value]="dateValue"\n            (dayClick)="onDayClick($event)"\n            (mousedown.silent.prevent)="(0)"\n        ></tui-calendar>\n    </ng-template>\n\n    <ng-template #input>\n        <form\n            class="form"\n            [formGroup]="testForm"\n        >\n            <tui-input\n                automation-id="tui-data-list-email-field"\n                formControlName="testValue"\n            >\n                Email\n            </tui-input>\n        </form>\n    </ng-template>\n\n    <ng-template #currency>\n        <div class="example">\n            <div\n                tuiGroup\n                class="group"\n            >\n                <tui-input [(ngModel)]="dollar">1 Rub = (X) Dollars</tui-input>\n                <tui-input [(ngModel)]="euro">1 Rub = (Y) Euros</tui-input>\n            </div>\n\n            <ul class="exchange tui-list tui-list_large">\n                <li class="tui-list__item">\n                    <tui-money\n                        currency="USD"\n                        [value]="moneyValue / dollar"\n                    ></tui-money>\n                </li>\n                <li class="tui-list__item">\n                    <tui-money\n                        currency="EUR"\n                        [value]="moneyValue / euro"\n                    ></tui-money>\n                </li>\n            </ul>\n        </div>\n    </ng-template>\n\n    <ng-template #range>\n        <tui-input-date-range\n            automation-id="tui-data-list-range-field"\n            class="form"\n            [(ngModel)]="rangeValue"\n        >\n            Range\n        </tui-input-date-range>\n    </ng-template>\n</ng-template>\n\n<div class="example">\n    <p>Email: {{ testValue }}</p>\n    <p>Chosen date: {{ dateValue }}</p>\n    <p>Range date: {{ rangeValue }}</p>\n    <p>Dol - {{ dollar }}, Eur - {{ euro }}</p>\n</div>\n'}}]);