(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[81002],{81002:n=>{n.exports='<section>\n    <tui-chip\n        *ngFor="let size of sizes"\n        [size]="size"\n        [style.border-radius.rem]="5"\n    >\n        Text\n    </tui-chip>\n</section>\n\n<section>\n    <tui-chip\n        *ngFor="let size of sizes"\n        iconStart="@tui.bell"\n        [size]="size"\n    >\n        Single icon\n    </tui-chip>\n</section>\n\n<section>\n    <tui-chip\n        *ngFor="let size of sizes"\n        iconEnd="@tui.circle-help"\n        iconStart="@tui.bell"\n        [size]="size"\n        [style.border-radius.rem]="5"\n    >\n        Duo icon\n    </tui-chip>\n</section>\n\n<section>\n    <tui-chip\n        *ngFor="let size of sizes"\n        [size]="size"\n    >\n        <img\n            *ngIf="size === \'m\' || size === \'s\'"\n            alt="Avatar"\n            src="/assets/images/avatar.jpg"\n            [style.border-radius.rem]="size === \'m\' ? 0.25 : 0.5"\n        />\n        Image\n    </tui-chip>\n</section>\n\n<section>\n    <tui-chip\n        *ngFor="let size of sizes"\n        [size]="size"\n        [style.border-radius.rem]="5"\n    >\n        <tui-avatar\n            *ngIf="size === \'m\' || size === \'s\'"\n            src="/assets/images/avatar.jpg"\n        />\n        Avatar\n    </tui-chip>\n</section>\n\n<section>\n    <tui-chip size="m">\n        Button\n        <button\n            iconStart="@tui.x"\n            size="s"\n            tuiIconButton\n        >\n            Remove\n        </button>\n    </tui-chip>\n    <tui-chip\n        *ngFor="let size of sizes | slice: 1"\n        [size]="size"\n    >\n        Button\n        <button\n            iconStart="@tui.x"\n            tuiIconButton\n        >\n            Remove\n        </button>\n    </tui-chip>\n</section>\n'}}]);