(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[84201],{84201:n=>{n.exports='<label\n    tuiBlock\n    tuiTheme="dark"\n>\n    <input\n        tuiCheckbox\n        type="checkbox"\n        [(ngModel)]="show"\n    />\n    Show more content\n</label>\n<tui-bottom-sheet [stops]="[\'9.5rem\', \'18.5rem\', \'100%\']">\n    <header class="header">\n        Taiga UI\n        <a\n            href="https://github.com/taiga-family/taiga-ui"\n            iconStart="@tui.star"\n            rel="noopener noreferrer"\n            size="m"\n            target="_blank"\n            tuiButton\n        >\n            Give us a Star\n        </a>\n    </header>\n    <ng-container *ngIf="show">\n        <img\n            alt=""\n            src="assets/images/taiga-family.png"\n            [style.inline-size.%]="100"\n            [style.margin-block-start.rem]="2"\n        />\n        <p *tuiRepeatTimes="let index of 20">All work and no play makes Jack a dull boy</p>\n        <tui-accordion>\n            <button tuiAccordion>Show more</button>\n            <tui-expand>All work and no play makes Jack a dull boy</tui-expand>\n        </tui-accordion>\n    </ng-container>\n</tui-bottom-sheet>\n'}}]);