(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[88692],{88692:n=>{n.exports='<h3>Checkbox</h3>\n<section>\n    <tui-chip\n        *tuiRepeatTimes="let i of checked.length"\n        appearance=""\n    >\n        Checkbox {{ i + 1 }}\n        <input\n            appearance="whiteblock"\n            tuiChip\n            type="checkbox"\n            [(ngModel)]="checked[i]"\n        />\n    </tui-chip>\n</section>\n\n<h3>Radio</h3>\n<section>\n    <tui-chip\n        *tuiRepeatTimes="let i of 3"\n        appearance=""\n    >\n        Radio {{ i + 1 }}\n        <input\n            appearance="whiteblock"\n            name="radio"\n            tuiChip\n            type="radio"\n            [value]="i"\n        />\n    </tui-chip>\n</section>\n\n<h3>Label whiteblock</h3>\n<section>\n    <label\n        *tuiRepeatTimes="let i of checked.length"\n        appearance="whiteblock"\n        tuiChip\n    >\n        <input\n            tuiCheckbox\n            type="checkbox"\n            [(ngModel)]="checked[i]"\n        />\n        Label {{ i + 1 }}\n    </label>\n</section>\n\n<h3>Label accent</h3>\n<section>\n    <label\n        *tuiRepeatTimes="let i of checked.length"\n        tuiChip\n        [appearance]="checked[i] ? \'accent\' : \'neutral\'"\n    >\n        <input\n            hidden\n            type="checkbox"\n            [(ngModel)]="checked[i]"\n        />\n        Label {{ i + 1 }}\n    </label>\n</section>\n\n<h3>Button</h3>\n<section>\n    <button\n        *tuiRepeatTimes="let i of 3"\n        tuiChip\n        (click)="onChip(i)"\n    >\n        Button {{ i + 1 }}\n        <button\n            iconStart="@tui.x"\n            tuiIconButton\n            (click.stop)="onX(i)"\n        >\n            Remove\n        </button>\n    </button>\n</section>\n\n<h3>Input</h3>\n<section>\n    <tui-chip\n        *tuiRepeatTimes="let i of values.length"\n        appearance=""\n        [style.color]="\'transparent\'"\n    >\n        {{ values[i] }}\n        <input\n            tuiChip\n            [(ngModel)]="values[i]"\n        />\n    </tui-chip>\n</section>\n'}}]);