(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[34811],{34811:n=>{n.exports='<form\n    class="wrapper"\n    [formGroup]="testForm"\n>\n    <label>\n        What is your address?\n\n        <tui-select\n            formControlName="email"\n            class="tui-space_bottom-4"\n            [tuiTextfieldLabelOutside]="true"\n        >\n            Choose an address\n            <tui-data-list *tuiDataList>\n                <button\n                    *ngIf="!signatureVisible"\n                    tuiOption\n                    class="link"\n                    (click)="toggle()"\n                >\n                    <tui-icon\n                        icon="@tui.circle-plus"\n                        class="icon"\n                    />\n                    Add signature\n                </button>\n                <button\n                    *ngFor="let item of items"\n                    tuiOption\n                    [value]="item"\n                >\n                    {{ item }}\n                </button>\n            </tui-data-list>\n        </tui-select>\n    </label>\n    <ng-container *ngIf="signatureVisible">\n        <tui-textarea\n            formControlName="signature"\n            tuiAutoFocus\n            tuiTextfieldSize="m"\n            class="tui-space_bottom-4"\n        >\n            Type a signature\n        </tui-textarea>\n        <button\n            size="m"\n            tuiButton\n            type="button"\n            (click)="toggle()"\n        >\n            Hide signature\n        </button>\n    </ng-container>\n</form>\n'}}]);