(self.webpackChunk_taiga_ui_components=self.webpackChunk_taiga_ui_components||[]).push([[44929],{44929:n=>{n.exports='<div tuiCell>\n    <div tuiTitle>\n        Single action\n        <div tuiSubtitle>Description of the action</div>\n    </div>\n    <button tuiButton>Action</button>\n</div>\n\n<div tuiCell>\n    <div tuiTitle>\n        Multiple actions\n        <div tuiSubtitle>With no content on the right</div>\n    </div>\n    <tui-hosted-dropdown\n        tuiDropdownAlign="right"\n        [content]="dropdown"\n    >\n        <button\n            appearance="icon"\n            iconLeft="tuiIconMoreHorizontalLarge"\n            tuiIconButton\n        >\n            Action\n        </button>\n        <ng-template\n            #dropdown\n            let-close="close"\n        >\n            <tui-data-list-wrapper\n                [items]="items"\n                (itemClick)="close()"\n            ></tui-data-list-wrapper>\n        </ng-template>\n    </tui-hosted-dropdown>\n</div>\n\n<button tuiCell>\n    <div tuiTitle>\n        Multiple actions\n        <div tuiSubtitle>When there\'s content on the right</div>\n    </div>\n    <div\n        tuiCellActions\n        tuiGroup\n        tuiTheme="night"\n        [collapsed]="true"\n    >\n        <button\n            *ngFor="let item of items"\n            appearance="opposite"\n            tuiIconButton\n            [iconLeft]="item.icon"\n        >\n            {{ item }}\n        </button>\n    </div>\n    <div tuiTitle>\n        Hover over\n        <div tuiSubtitle>Put it before the right side</div>\n    </div>\n</button>\n'}}]);