(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[71805],{71805:n=>{n.exports='<button\n    tuiButton\n    type="button"\n    (click)="open = true"\n>\n    Show\n</button>\n\n<ng-template\n    let-observer\n    [tuiSheetDialogOptions]="{appearance: \'fullscreen\'}"\n    [(tuiSheetDialog)]="open"\n>\n    <header tuiHeader>\n        <hgroup tuiTitle>\n            <h2>Title</h2>\n            <p tuiSubtitle>Subtitle</p>\n            <p [style.margin-block]="\'0.75rem 0\'">\n                <label tuiLabel>\n                    <input\n                        tuiSwitch\n                        type="checkbox"\n                        [(ngModel)]="floating"\n                    />\n                    Floating visibility\n                </label>\n            </p>\n            <p>\n                <label tuiLabel>\n                    <input\n                        tuiSwitch\n                        type="checkbox"\n                        [(ngModel)]="secondAction"\n                    />\n                    Second action visibility\n                </label>\n            </p>\n            <p>\n                <input\n                    placeholder="Some search"\n                    tuiSearch\n                    type="search"\n                    class="input"\n                    [(ngModel)]="search"\n                />\n            </p>\n        </hgroup>\n    </header>\n    <div class="content">\n        <section class="items">\n            @for (item of items | tuiFilter: filter : search; track item) {\n                <div tuiCell>\n                    <div\n                        appearance="primary"\n                        tuiAvatar="@tui.star"\n                    ></div>\n                    <div tuiTitle>\n                        {{ item.title }}\n                        <div tuiSubtitle>{{ item.description }}</div>\n                    </div>\n                </div>\n            }\n        </section>\n\n        @if (floating) {\n            <footer\n                tuiFloatingContainer\n                @tuiSlideInTop\n            >\n                <button\n                    tuiButton\n                    type="button"\n                    (click)="observer.complete()"\n                >\n                    Main action\n                </button>\n                @if (secondAction) {\n                    <button\n                        appearance="flat"\n                        tuiButton\n                        type="button"\n                        @tuiHeightCollapse\n                        (click)="observer.complete()"\n                    >\n                        Secondary action\n                    </button>\n                }\n            </footer>\n        }\n    </div>\n</ng-template>\n'}}]);