(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[84352],{84352:n=>{n.exports=":host {\n    ::ng-deep {\n        tui-input-card-group {\n            inline-size: 100% !important;\n\n            &.without-date {\n                .t-wrapper_expire {\n                    display: none;\n                }\n\n                tui-icon[icon='@tui.x'] {\n                    display: none;\n                }\n            }\n\n            .t-value_collapsed {\n                letter-spacing: 0.1px;\n            }\n        }\n    }\n}\n\n.modal-container {\n    inline-size: 30rem;\n}\n\n@media screen and (max-width: 599px) {\n    .modal-container {\n        inline-size: 100%;\n    }\n}\n\n.title {\n    margin: 0 0 1rem;\n    font-weight: 500;\n    font-size: 1.125rem;\n    line-height: 1.625rem;\n    letter-spacing: 0.01125rem;\n}\n\n.form-block {\n    position: relative;\n    margin-block-end: 1rem;\n\n    .saved-card-preset {\n        &__item {\n            display: flex;\n            block-size: 3.5rem;\n            background: #fafbfb;\n            border-radius: 1rem;\n            border: 2px solid #eaeaea;\n            padding: 0 1rem;\n            align-items: center;\n            cursor: pointer;\n\n            &__placeholder {\n                color: #717172;\n                letter-spacing: 0.015rem;\n                font-size: 0.75rem;\n                line-height: 1.125rem;\n            }\n\n            &__info {\n                display: flex;\n                align-items: center;\n\n                &__icon {\n                    inline-size: 2.25rem;\n                    block-size: 1.375rem;\n                    background-position: center;\n                    background-repeat: no-repeat;\n                    margin-inline-end: 0.5rem;\n                }\n\n                &__number {\n                    letter-spacing: 0.2px;\n                    line-height: 1.375rem;\n                    color: #313132;\n                }\n            }\n        }\n    }\n\n    &.is--links {\n        display: flex;\n        align-items: center;\n\n        ::ng-deep {\n            a {\n                font-weight: normal;\n                font-size: 0.875rem;\n                line-height: 1.25rem;\n\n                &:first-child {\n                    color: #6496dc;\n                }\n\n                &:last-child {\n                    color: #b0b0b0;\n                    margin-inline-start: auto;\n                }\n            }\n        }\n    }\n}\n\n.fake-input {\n    position: absolute;\n    bottom: 0;\n    left: 0;\n    z-index: -9999999;\n    inline-size: 100%;\n    caret-color: transparent;\n    cursor: none;\n    color: transparent;\n}\n"}}]);