(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[9145],{9145:n=>{n.exports='<h3>Multi select control</h3>\n<button\n    appearance="flat"\n    tuiButtonSelect\n    tuiChevron\n    tuiIconButton\n    type="button"\n    [tuiAppearanceState]="open ? \'hover\' : null"\n    [(ngModel)]="value"\n    [(open)]="open"\n>\n    Open\n    <tui-data-list\n        *tuiDropdown\n        size="l"\n        [style.width.rem]="12"\n    >\n        <tui-opt-group tuiMultiSelectGroup>\n            <tui-opt-group label="Main dishes menu with long label">\n                @for (burger of burgers; track burger) {\n                    <button\n                        new\n                        tuiOption\n                        type="button"\n                        [value]="burger"\n                    >\n                        {{ burger }}\n                    </button>\n                }\n            </tui-opt-group>\n            <tui-opt-group label="Drinks">\n                @for (drink of drinks; track drink) {\n                    <button\n                        new\n                        tuiOption\n                        type="button"\n                        [value]="drink"\n                    >\n                        {{ drink }}\n                    </button>\n                }\n            </tui-opt-group>\n        </tui-opt-group>\n    </tui-data-list>\n</button>\n\n<p>{{ value }}</p>\n<h3>Separate toggles</h3>\n<button\n    appearance="flat"\n    tuiChevron\n    tuiIconButton\n    type="button"\n    [tuiAppearanceState]="label ? \'hover\' : null"\n    [tuiDropdown]="separate"\n    [tuiDropdownMaxHeight]="500"\n    [(tuiDropdownOpen)]="label"\n>\n    Open\n</button>\n<ng-template #separate>\n    <tui-data-list [style.width.rem]="10">\n        <label tuiOption>\n            First\n            <input\n                tuiCheckbox\n                type="checkbox"\n                [(ngModel)]="first"\n            />\n        </label>\n        <label tuiOption>\n            Second\n            <input\n                tuiSwitch\n                type="checkbox"\n                [(ngModel)]="second"\n            />\n        </label>\n    </tui-data-list>\n</ng-template>\n'}}]);