(self.webpackChunkdemo=self.webpackChunkdemo||[]).push([[9916],{9916:n=>{n.exports="import {Component, inject} from '@angular/core';\nimport {\n    type AsyncValidatorFn,\n    FormControl,\n    FormGroup,\n    ReactiveFormsModule,\n    Validators,\n} from '@angular/forms';\nimport {changeDetection} from '@demo/emulate/change-detection';\nimport {encapsulation} from '@demo/emulate/encapsulation';\nimport {TUI_IS_E2E} from '@taiga-ui/cdk';\nimport {TuiError, TuiTextfield} from '@taiga-ui/core';\nimport {TuiForm} from '@taiga-ui/layout';\nimport {delay, of} from 'rxjs';\n\nfunction asyncValidatorFn(isE2E: boolean): AsyncValidatorFn {\n    return ({value}) =>\n        value && /^[a-zA-Z]+$/.test(value)\n            ? of(null)\n            : of({error: 'Only latin letters allowed'}).pipe(delay(isE2E ? 0 : 3000));\n}\n\n@Component({\n    imports: [ReactiveFormsModule, TuiError, TuiForm, TuiTextfield],\n    templateUrl: './index.html',\n    encapsulation,\n    changeDetection,\n})\nexport default class Example {\n    protected readonly form = new FormGroup({\n        text: new FormControl(\n            'русский текст',\n            [Validators.required],\n            [asyncValidatorFn(inject(TUI_IS_E2E))],\n        ),\n    });\n}\n"}}]);